import FileCloak from '../FileCloak.webp';
import './Login.css';

function Login() {

  const navigate = useNavigate();

  const handleNavigation = (path) => {
    navigate(path);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add form submission logic here
    console.log("Form submitted");
  };

  return (
    <>
    <div className="login-body">
      <div className="login-container" id="loginForm">
          <div className="logo-container">
              <img src={FileCloak} className="login-logo" alt="FileCloak" />
          </div>
          <form className="signup-form" onSubmit={handleSubmit}>
              <div className="input-group">
                  <input type="text" id="emailLogin" name="emailLogin" placeholder="Email" required></input>
                  <i className="icon username-icon"></i> 
              </div>
              <div className="input-group">
                  <input type="password" id="passwordLogin" name="passwordLogin" placeholder="Password" required></input>
                  <i className="icon password-icon"></i> 
              </div>
              <button type="submit" className="login-submit-btn" value="Login">LOGIN</button>
              <button
              className="signup-btn-in-login"
              onClick={(event) => {
                event.preventDefault();
                handleNavigation('/signup');
              }}
            >
              Sign Up
            </button>
          </form>
      </div>
    </div>
    </>
  );
}

export default Login;
