import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import FileCloak from '../FileCloak.webp';
import './Login.css';

function Login() {
  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleNavigation = (path) => {
    navigate(path);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const email = event.target.emailLogin.value;
    const password = event.target.passwordLogin.value;

    try {
      const response = await fetch('http://localhost:4000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.status === 401) {
        const data = await response.json();
        setError(data.message); // Handle the error message accordingly
      } else if (response.ok) {
        const data = await response.json();
      
        // Store the token in localStorage
        localStorage.setItem('authToken', data.token);
      
        // Navigate based on the user role
        if (data.role === 'user') {
          alert('Sukses Login');
          handleNavigation('/userpage');
        } else if (data.role === 'admin') {
          alert('Sukses Login');
          handleNavigation('/encryptfile');
        }
        
      } 
    } catch (error) {
      console.error('Error:', error);
      setError('Server error. Please try again later.');
    }
  };

  return (
    <>
      <div className="login-body">
        <div className="login-container" id="loginForm">
          <div className="logo-container">
            <img src={FileCloak} className="login-logo" alt="FileCloak" />
          </div>
          <form className="login-form" onSubmit={handleSubmit}>
            <div className="login-input-group">
              <input
                className="login-email"
                type="text"
                id="emailLogin"
                name="emailLogin"
                placeholder="Email"
                required
              />
            </div>
            <div className="login-input-group">
              <input
                className="login-password"
                type="password"
                id="passwordLogin"
                name="passwordLogin"
                placeholder="Password"
                required
              />
            </div>
            <button type="submit" className="login-submit-btn">
              Log In
            </button>
            <button
              className="signup-btn-in-login"
              onClick={(event) => {
                event.preventDefault();
                handleNavigation('/signup');
              }}
            >
              Sign Up
            </button>
          </form>
          {error && <p className="error-message">{error}</p>}
        </div>
      </div>
    </>
  );
}

export default Login;
