import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import FileCloak from '../FileCloak.webp';
import './AdminPageEncryptFile.css';
import CryptoJS from 'crypto-js';
import { auth } from '../firebase'; // Import the initialized auth object

function AdminPageEncryptFile() {
  const navigate = useNavigate();
  const [encryptionKey, setEncryptionKey] = useState('');
  const [note, setNote] = useState('');
  const [files, setFiles] = useState(null);
  const [zipFiles, setZipFiles] = useState(false);
  const [encryptedLinks, setEncryptedLinks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const user = auth.currentUser;
    // Redirect if user is not authenticated or not an admin
    if (!user || user.role !== 'admin') { // Assuming you are able to access role directly (implement role fetching as needed)
      navigate('/userpage'); // Redirect to user page or show an error message
    }
  }, [navigate]);

  const handleNavigation = (path) => {
    navigate(path);
  };

  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };

  const handleEncrypt = async (event) => {
    event.preventDefault();

    if (!files) {
      setError('Please select files to upload.');
      return;
    }

    if (!encryptionKey) {
      setError('Please provide an encryption key.');
      return;
    }

    const formData = new FormData();
    for (const file of files) {
      formData.append('files', file);
    }
    formData.append('key', encryptionKey);
    formData.append('note', note);
    formData.append('zipFiles', zipFiles);

    try {
      const response = await fetch('http://localhost:4000/encryptfile', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setEncryptedLinks(data.encryptedLinks);
        setError('');
      } else {
        setError(data.message);
      }
    } catch (error) {
      setError('An error occurred: ' + error.message);
    }
  };

  const handleKeyChange = (event) => {
    const key = event.target.value.slice(0, 64);
    setEncryptionKey(key);
  };

  const handleZipCheckboxChange = () => {
    setZipFiles(!zipFiles);
  };

  return (
    <>
      <div className='encrypt-file-body'>
        <div className="encrypt-file-container" id="encryptForm">
          <div className="logoutButton">
            <button
              type="button"
              className="logout-btn"
              id="logoutButton"
              onClick={() => handleNavigation('/logout')}
            >
              Logout
            </button>
            <br />
          </div>
          <header>
            <img src={FileCloak} className="encrypt-file-logo" alt="FileCloak" />
          </header>
          <form className="encrypt-file-form" onSubmit={handleEncrypt}>
            <div className="encrypt-file-panel">
              <div className="file-encryption">
                <h2 className='header2'>Input File</h2>
                <div>
                  <button
                    type="button"
                    className="encrypt-text-btn"
                    id="encryptTextButton"
                    onClick={() => handleNavigation('/encrypt-text')}
                  >
                    Encrypt Text
                  </button>
                  <br />
                </div>
                <div className="file-input-div">
                  <div className="drop-area">
                    <p>Drag and Drop files here or click to browse</p>
                  </div>
                  <div className="zip-checkbox-container">
                    <label>
                      <input
                        className="checkbox"
                        type="checkbox"
                        id="zipFilesCheckbox"
                        checked={zipFiles}
                        onChange={handleZipCheckboxChange}
                      />{' '}
                      Zip Files
                    </label>
                  </div>
                  <input
                    className="file-input"
                    type="file"
                    id="fileInput"
                    name="file"
                    multiple
                    required
                    onChange={handleFileChange}
                  />
                </div>

                <div>
                  <textarea
                    type='text'
                    className='counter'
                    id="keyGen"
                    rows="3"
                    cols="60"
                    placeholder="You can also input a 64 character long key"
                    maxLength="64"
                    value={encryptionKey}
                    onChange={handleKeyChange}
                  ></textarea>
                  <br />
                  <p className='counter-tracker'>
                    <span id="counter">{encryptionKey.length}</span> / 64 characters
                  </p>
                  <button
                    className='keygen-btn'
                    id="keyGenButton"
                    type="button"
                    onClick={() => setEncryptionKey(CryptoJS.lib.WordArray.random(64).toString())}
                  >
                    Generate Key
                  </button>
                  <br />
                  <button
                    className='copy-btn'
                    id="copyButton"
                    type="button"
                    onClick={() => navigator.clipboard.writeText(encryptionKey)}
                  >
                    Copy to Clipboard
                  </button>
                </div>
                <div>
                  <label>Note:</label>
                  <textarea
                    id="note"
                    name="note"
                    rows="3"
                    cols="50"
                    placeholder="Add a note..."
                    value={note}
                    onChange={(e) => setNote(e.target.value)}
                  ></textarea>
                </div>

                {/* Container to hold multiple encrypted outputs */}
                <div id="encryptedOutputsContainer">
                  {encryptedLinks.map((link, index) => (
                    <div key={index} className="encrypted-link">
                      <a href={link} target="_blank" rel="noopener noreferrer">
                        Encrypted File {index + 1}
                      </a>
                    </div>
                  ))}
                </div>

                <button type="submit" className="encrypt-btn" id="encryptButton">
                  Encrypt
                </button>
                {error && <p className="error-message">{error}</p>}
              </div>
              <div>
                <div>
                  <button
                    type="button"
                    className="decrypt-btn"
                    id="decryptButton"
                    onClick={() => handleNavigation('/decrypt')}
                  >
                    Decrypt
                  </button>
                  <br />
                </div>
                <br />
                <div>
                  <button
                    type="button"
                    className="list-btn"
                    id="listButton"
                    onClick={() => handleNavigation('/list')}
                  >
                    Move to List
                  </button>
                  <br />
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

export default AdminPageEncryptFile;
